SELECT TRANS.STATEMENT_ID AS TRANS_STATMNT_ID, TRANS.PRCS_TRNS_CD AS TRANS_PRCS_TRNS_CD, TRANS.QUARTER_ID AS TRANS_QUARTER_ID, CLM.MODEL_TYP_CD AS MODEL_TYP_CD, CLM.PICO_ID AS CLM_PICO_ID , CLM.RBAT_ID AS CLM_RBAT_ID, CLM.RAC_ID AS CLM_RAC_ID, CLM.EXTL_LVL1_ID AS CLM_EXTL_LVL1_ID, CLM.EXTL_LVL2_ID AS CLM_EXTL_LVL2_ID, CLM.EXTL_LVL3_ID AS CLM_EXTL_LVL3_ID, CLM.NDC_ID AS CLM_NDC_ID, TO_CHAR(CLM.DSPN_DT,'YYYY') AS CLM_DSPN_YR, SUM(TRANS.ADMN_ADJ_AMT) AS TRANS_ADMN_ADJ_AMT, SUM(TRANS.BASE_ADJ_AMT) AS TRANS_BASE_ADJ_AMT, SUM(TRANS.FRMLY_ADJ_AMT) AS TRANS_FRMLY_ADJ_AMT, SUM(TRANS.CNTRCT_ADJ_AMT) AS TRANS_CNTRCT_ADJ_AMT, SUM(TRANS.PRC_PTCT_ADJ_AMT) AS TRANS_PRC_PTCT_ADJ_AMT , SUM(TRANS.PERFC_ADJ_AMT) AS TRANS_PERFC_ADJ_AMT FROM VRAP. TRPS_RPT_CLM_TRANS_DTL TRANS , VRAP.TRPS_RPT_CLM_DTL CLM WHERE TRANS.CLM_GID = CLM.CLM_GID
AND TRANS.STATEMENT_ID IN (SELECT DISTINCT STATEMENT_ID FROM VRAP.TRPS_RPT_ADJ_PMT_CNTL WHERE PRCS_STAT_CD=20) GROUP BY TRANS.STATEMENT_ID, TRANS.PRCS_TRNS_CD, TRANS.QUARTER_ID, CLM.MODEL_TYP_CD, CLM.PICO_ID, CLM.RBAT_ID, CLM.RAC_ID, CLM.EXTL_LVL1_ID, CLM.EXTL_LVL2_ID, CLM.EXTL_LVL3_ID, CLM.NDC_ID, TO_CHAR(CLM.DSPN_DT, 'YYYY') WITH UR;
