try.command:
int gdxc1 = script.getProperty("gdx_tfh_count.1").intValue();
int gdxc2 = script.getProperty("gdx_tb_count.1").intValue();
int rbtc1 = script.getProperty("rbt_tfh_count.1").intValue();
int rbtc2 = script.getProperty("rbt_tb_count.1").intValue();
System.out.println("TPB_FORMULARY_HIST - Oracle Count: " + rbtc1 + "  DB2 Count: " + gdxc1);
System.out.println("TPRESCRIP_BENEFIT  - Oracle Count: " + rbtc2 + "  DB2 Count: " + gdxc2);
if ((gdxc1 != rbtc1) || (gdxc2 != rbtc2)) {
	throw new Exception(
		"Counts do not match:\n" +
		"TPB_FORMULARY_HIST - Oracle Count: " + rbtc1 + "  DB2 Count: " + gdxc1 + "\n" +
		"TPRESCRIP_BENEFIT  - Oracle Count: " + rbtc2 + "  DB2 Count: " + gdxc2);
}
